import{gsap}from"gsap";import{GSDevTools}from"gsap/GSDevTools";import{MorphSVGPlugin}from"gsap/MorphSVGPlugin";import{DrawSVGPlugin}from"gsap/DrawSVGPlugin";import{MotionPathHelper}from"gsap/MotionPathHelper";gsap.registerPlugin(GSDevTools,MorphSVGPlugin,DrawSVGPlugin,MotionPathHelper);const mainTL=gsap.timeline();function setStage(){let i=gsap.timeline();return i.set(".swirl",{x:"-=175",alpha:0}).set(".side",{x:"-=175",alpha:0}).set(".handle",{x:"-=175",alpha:0}),i}function firstAnimation(){let i=gsap.timeline();return i.to(".swirl",{duration:3,x:0,alpha:1}),i}mainTL.add(setStage()).add(firstAnimation()),GSDevTools.create();